interface SavedBirthdays {
    [userId: string]: string;
}

const Toast = findByPropsLazy("showToast", "createToast");

function getBirthdayBadgeComponent() {
    return (
        <div style={{ transform: "scale(0.80)" }}>
            <CakeIcon width={20} height={20} />
        </div>
    );
}

function shouldShowBirthdayBadge(info: BadgeUserArgs) {
    return checkBirthday(info.userId);
}

const birthdayBadge: ProfileBadge = {
    component: getBirthdayBadgeComponent,
    shouldShow: shouldShowBirthdayBadge,
};

const logger = new Logger("CakeDay");

let savedBirthdays: SavedBirthdays = {};

async function loadBirthdays() {
    savedBirthdays = await DataStore.get("CakeDay_birthdays") ?? {};
}

async function saveBirthdays() {
    await DataStore.set("CakeDay_birthdays", savedBirthdays);
}

function checkBirthday(userId: string): boolean {
    const today = new Date();
    const birthday = savedBirthdays[userId];

    if (!birthday) return false;

    const [dayStr, monthStr] = birthday.split("/");
    const [day, month] = [parseInt(dayStr, 10), parseInt(monthStr, 10)];

    return today.getDate() === day && (today.getMonth() + 1) === month;
}

function isValidBirthday(birthday: string): boolean {
    const pattern = /^(0[1-9]|[12][0-9]|3[01])\/(0[1-9]|1[0-2])$/;
    return pattern.test(birthday);
}

async function setBirthday(userId: string, username: string, birthday: string) {
    if (isValidBirthday(birthday)) {
        savedBirthdays[userId] = birthday;
        await saveBirthdays();

        Toast.showToast(Toast.createToast("Birthday set successfully!", Toast.Type.SUCCESS));
        logger.info(`Set birthday for ${username} (${userId}) to ${birthday}`);
    } else {
        Toast.showToast(Toast.createToast("Invalid birthday format! Only DD/MM is accepted.", Toast.Type.FAILURE));
    }
}

async function clearBirthday(userId: string, username: string) {
    if (savedBirthdays[userId]) {
        delete savedBirthdays[userId];
        await saveBirthdays();

        Toast.showToast(Toast.createToast("Birthday cleared successfully!", Toast.Type.SUCCESS));
        logger.info(`Cleared birthday for ${username} (${userId})`);
    }
}

function BirthdayModal({ userId, username, modalProps }: { userId: string, username: string, modalProps: any; }) {
    const [birthdayInput, setBirthdayInput] = React.useState("");
    const [loading, setLoading] = React.useState(false);

    return (
        <ModalRoot {...modalProps} className="vc-cakeday-modal">
            <ModalHeader>
                <Forms.FormTitle tag="h2">Set Birthday for {username}</Forms.FormTitle>
            </ModalHeader>
            <ModalContent>
                <Forms.FormSection>
                    <Forms.FormText className={Margins.bottom8}>
                        Enter the birthday in DD/MM format (e.g. 25/12)
                    </Forms.FormText>
                    <TextInput
                        placeholder="e.g. 25/12"
                        value={birthdayInput}
                        onChange={setBirthdayInput}
                        autoFocus
                        disabled={loading}
                    />
                    {savedBirthdays[userId] && (
                        <Forms.FormText className={Margins.top8}>
                            Current birthday: {savedBirthdays[userId]}
                        </Forms.FormText>
                    )}
                </Forms.FormSection>
            </ModalContent>
            <ModalFooter>
                <Button
                    color={Button.Colors.BRAND}
                    onClick={async () => {
                        setLoading(true);
                        await setBirthday(userId, username, birthdayInput);
                        setLoading(false);
                        modalProps.onClose();
                    }}
                    disabled={loading || !birthdayInput.trim()}
                >
                    Set Birthday
                </Button>
                <Button
                    color={Button.Colors.PRIMARY}
                    look={Button.Looks.LINK}
                    onClick={() => {
                        if (!loading) modalProps.onClose();
                    }}
                    disabled={loading}
                >
                    Cancel
                </Button>
            </ModalFooter>
        </ModalRoot>
    );
}

