function CakeDaySettings() {
    const [birthdays, setBirthdays] = React.useState(savedBirthdays);

    React.useEffect(() => {
        const interval = setInterval(() => {
            setBirthdays({ ...savedBirthdays });
        }, 1000);

        return () => clearInterval(interval);
    }, []);

    if (Object.keys(birthdays).length === 0) {
        return (
            <Forms.FormSection>
                <Forms.FormTitle tag="h3">Saved Birthdays</Forms.FormTitle>
                <Forms.FormText>
                    No birthdays saved yet. Right-click on users to add their birthdays!
                </Forms.FormText>
            </Forms.FormSection>
        );
    }

    return (
        <Forms.FormSection>
            <Forms.FormTitle tag="h3">Saved Birthdays</Forms.FormTitle>
            {Object.entries(birthdays).map(([userId, birthday]) => {
                const user = UserStore.getUser(userId);
                const isBirthday = checkBirthday(userId);

                return (
                    <div key={userId} className={Margins.bottom8}>
                        <div style={{ display: "flex", alignItems: "center", gap: "8px" }}>
                            <Forms.FormText>
                                <strong>{user?.username || "Unknown User"}</strong> - {birthday}
                                {isBirthday && <span style={{ marginLeft: "8px" }}>ðŸŽ‚ Today!</span>}
                            </Forms.FormText>
                            <Button
                                size={Button.Sizes.TINY}
                                color={Button.Colors.RED}
                                look={Button.Looks.OUTLINED}
                                onClick={() => clearBirthday(userId, user?.username || "Unknown")}
                            >
                                Remove
                            </Button>
                        </div>
                    </div>
                );
            })}
        </Forms.FormSection>
    );
}

const cakeLocations = {
    chat: {
        description: "next to usernames in chat",
        onEnable: () => {
            if (Settings.plugins.CakeDay.chat) {
                addMessageDecoration("vc-cakeday", props => {
                    if (!props.message?.author.id) return null;
                    if (!checkBirthday(props.message?.author.id)) return null;
                    return (
                        <ErrorBoundary noop>
                            <CakeIcon width={20} height={20} />
                        </ErrorBoundary>
                    );
                });
            }
        },
        onDisable: () => removeMessageDecoration("vc-cakeday")
    },
    memberList: {
        description: "in member list",
        onEnable: () => {
            if (Settings.plugins.CakeDay.memberList) {
                addMemberListDecorator("vc-cakeday", props => {
                    if (!props.user || !props.user.id) return null;
                    if (!checkBirthday(props.user.id)) return null;
                    return (
                        <ErrorBoundary noop>
                            <CakeIcon width={16} height={16} />
                        </ErrorBoundary>
                    );
                });
            }
        },
        onDisable: () => removeMemberListDecorator("vc-cakeday")
    },
};

const options = {
    chat: {
        type: OptionType.BOOLEAN,
        description: "Show cake icons next to usernames in chat",
        restartNeeded: true,
        default: true
    } as const,
    memberList: {
        type: OptionType.BOOLEAN,
        description: "Show cake icons in member list",
        restartNeeded: true,
        default: true
    } as const,
    profileBadge: {
        type: OptionType.BOOLEAN,
        description: "Show birthday cake badge on user profiles",
        restartNeeded: true,
        default: true
    } as const
};

export default definePlugin({
    name: "CakeDay",
    description: "Track and display user birthdays with cake icons",
    authors: [Devs.LSDZaddi],

    options,
    settingsAboutComponent: () => <CakeDaySettings />,

    contextMenus: {
        "user-context": (children: any[], { user }: { user: any; }) => {
            if (!user) return;
            children.push(
                <Menu.MenuItem
                    id="cakeday-submenu"
                    label="Birthday"
                    icon={() => <span style={{ fontSize: "14px" }}>ðŸŽ‚</span>}
                >
                    <Menu.MenuItem
                        id="cakeday-set"
                        label="Set Birthday"
                        action={() => {
                            openModal(modalProps => (
                                <BirthdayModal
                                    userId={user.id}
                                    username={user.username}
                                    modalProps={modalProps}
                                />
                            ));
                        }}
                    />
                    <Menu.MenuItem
                        id="cakeday-clear"
                        label="Clear Birthday"
                        action={() => clearBirthday(user.id, user.username)}
                        disabled={!savedBirthdays[user.id]}
                    />
                    {savedBirthdays[user.id] && (
                        <Menu.MenuItem
                            id="cakeday-current"
                            label={`Current: ${savedBirthdays[user.id]}`}
                            disabled
                        />
                    )}
                </Menu.MenuItem>
            );
        }
    },

    async start() {
        await loadBirthdays();
        if (Settings.plugins.CakeDay.profileBadge) {
            Badges.addProfileBadge(birthdayBadge);
        }

        if (this.contextMenus && this.contextMenus["user-context"]) {
            addContextMenuPatch("user-context", this.contextMenus["user-context"]);
        }

        Object.entries(cakeLocations).forEach(([key, value]) => {
            if (Settings.plugins.CakeDay[key]) value.onEnable();
        });
    },

    stop() {
        if (Settings.plugins.CakeDay.profileBadge) {
            Badges.removeProfileBadge(birthdayBadge);
        }
        if (this.contextMenus && this.contextMenus["user-context"]) {
            removeContextMenuPatch("user-context", this.contextMenus["user-context"]);
        }
        Object.entries(cakeLocations).forEach(([_, value]) => {
            value.onDisable();
        });
    }
});
